INSERT INTO `mydb`.`recipes` (`user_id`, `title`, `ready_in_minutes`, `vegetarian`, `vegan`, `gluten_free`, `servings`, `instructions`, `ingredients`, `image`)
VALUES (1, 'Homemade Four Cheese Ravioli', 2, 0, 0, 0, 4, '<ol> <li>Mound flour and salt together on a work surface and form a well.</li> <li>Beat eggs, water, and olive oil in a bowl.</li> <li>Pour 1/2 of the egg mixture into the well.</li> <li>Begin mixing egg and flour with one hand; use your other hand to keep the flour mound steady.</li> <li>Add remaining egg mixture and knead to form a dough.</li> <li>Knead dough until smooth, 8 to 10 minutes; add more flour if the dough is too sticky.</li> <li>Form dough into a ball and wrap tightly with plastic.</li> <li>Refrigerate for 1 hour.</li> <li>While the dough is resting, prepare the ravioli filling: Mix ricotta cheese, cream cheese, mozzarella cheese, provolone cheese, egg, and parsley until well combined. Set the filling aside.</li> <li>Heat 2 tablespoons of olive oil in a skillet over medium heat.</li> <li>Stir in crushed garlic and pesto sauce and cook for one minute.</li> <li>Stir in heavy cream; increase the heat to high and bring to a boil.</li> <li>Reduce the heat and simmer for 5 minutes.</li> <li>Whisk in Parmesan cheese until melted.</li> <li>Remove the pan from the heat and keep warm.</li> <li>Meanwhile, warm marinara sauce over medium-low heat in a separate saucepan.</li> <li>Preheat the oven to 375 degrees F (190 degrees C).</li> <li>Beat egg and 1 tablespoon of water together in a small bowl to make the egg wash.</li> <li>Roll out the pasta dough into thin sheets no thicker than a nickel.</li> <li>To assemble the ravioli, brush the egg wash over a sheet of pasta.</li> <li>Drop cheese filling in 1-teaspoon portions onto the dough, 1-inch apart.</li> <li>Cover the filling with the top sheet of pasta; use your fingers to press around each portion of filling, making the seal as airtight as possible.</li> <li>Cut out individual ravioli with a knife or pizza cutter. Use your fingers to seal the edges.</li> <li>Fill a large pot with lightly salted water and bring to a rolling boil over high heat.</li> <li>Stir in the ravioli and return to a boil.</li> <li>Cook uncovered, stirring occasionally, until the ravioli float to the top and the filling is hot, 4 to 8 minutes.</li> <li>Drain well.</li> <li>Grease a baking sheet.</li> <li>Place the cooked ravioli on the baking sheet and bake in the preheated oven until brown, about 4 minutes.</li> <li>To serve, divide ravioli between four warmed serving bowls.</li> <li>Drizzle marinara sauce on top, followed by cream sauce.</li> </ol>', '[ { "id": 1, "name": "Ravioli Dough: 2 cups all-purpose flour" }, { "id": 2, "name": "Ravioli Dough: 1 pinch salt" }, { "id": 3, "name": "Ravioli Dough: 2 eggs" }, { "id": 4, "name": "Ravioli Dough: 1 ½ tablespoons water" }, { "id": 5, "name": "Ravioli Dough: 1 teaspoon olive oil" }, { "id": 6, "name": "Ravioli Filling: 1 (8 ounce) container ricotta cheese" }, { "id": 7, "name": "Ravioli Filling: 1 (4 ounce) package cream cheese, softened" }, { "id": 8, "name": "Ravioli Filling: ½ cup shredded mozzarella cheese" }, { "id": 9, "name": "Ravioli Filling: ½ cup shredded provolone cheese" }, { "id": 10, "name": "Ravioli Filling: 1 egg" }, { "id": 11, "name": "Ravioli Filling: 1 ½ teaspoons dried parsley" }, { "id": 12, "name": "Pesto-Alfredo Cream Sauce: 2 tablespoons olive oil" }, { "id": 13, "name": "Pesto-Alfredo Cream Sauce: 2 cloves garlic, crushed" }, { "id": 14, "name": "Pesto-Alfredo Cream Sauce: 3 tablespoons prepared basil pesto sauce" }, { "id": 15, "name": "Pesto-Alfredo Cream Sauce: 2 cups heavy cream" }, { "id": 16, "name": "Pesto-Alfredo Cream Sauce: ¼ cup grated Parmesan cheese" }, { "id": 17, "name": "Pesto-Alfredo Cream Sauce: 1 (24 ounce) jar marinara sauce" }, { "id": 18, "name": "Egg Wash: 1 egg" }, { "id": 19, "name": "Egg Wash: 1 tablespoon water" } ]', 'https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fimages.media-allrecipes.com%2Fuserphotos%2F9118201.jpg&q=60&c=sc&orient=true&poi=auto&h=512');
