INSERT INTO `mydb`.`recipes` (`user_id`, `title`, `ready_in_minutes`, `vegetarian`, `vegan`, `gluten_free`, `servings`, `instructions`, `ingredients`, `image`)
VALUES (2, 'Sheet Pan Salmon and Bell Pepper Dinner', 30, 0, 0, 1, 4, '<ol> <li>Preheat oven to 400 degrees F (200 degrees C). Grease a sheet pan with 2 tablespoons olive oil.</li> <li>Place salmon fillets, red and yellow bell peppers, and onion on the prepared sheet pan.</li> <li>Combine lemon juice, 3 tablespoons olive oil, water, maple syrup, garlic, salt, red pepper flakes, cumin, and parsley in a small bowl. Drizzle 2/3 of the sauce over the ingredients on the sheet pan.</li> <li>Bake in the preheated oven until salmon is cooked through and flakes easily with a fork, 10 to 15 minutes.</li> <li>Serve with lemon slices and remaining sauce.</li> </ol>', '[ { "id": 1, "name": "2 tablespoons olive oil" }, { "id": 2, "name": "4 (3 ounce) fillets salmon fillets" }, { "id": 3, "name": "2 red bell peppers, chopped" }, { "id": 4, "name": "1 yellow bell pepper, chopped" }, { "id": 5, "name": "1 onion, sliced" }, { "id": 6, "name": "Sauce:" }, { "id": 7, "name": "6 tablespoons lemon juice" }, { "id": 8, "name": "3 tablespoons olive oil" }, { "id": 9, "name": "2 tablespoons water" }, { "id": 10, "name": "1 tablespoon maple syrup" }, { "id": 11, "name": "5 cloves garlic" }, { "id": 12, "name": "1 ½ teaspoons salt" }, { "id": 13, "name": "1 ½ teaspoons red pepper flakes" }, { "id": 14, "name": "1 teaspoon ground cumin" }, { "id": 15, "name": "½ bunch fresh parsley, chopped" }, { "id": 16, "name": "1 lemon, sliced" } ]', 'https://imagesvc.meredithcorp.io/v3/mm/image?url=https%3A%2F%2Fimages.media-allrecipes.com%2Fuserphotos%2F4957773.jpg&q=60&c=sc&orient=true&poi=auto&h=512');
